const index = () => (
    <Layout>
<div>

    
<h1>hello next.js</h1>
{props.bpi.time.updated}
</div>
</Layout>
);

index.getInitialProps= async () =>{
    const res = await fetch ('https://api.coindesk.com/cq/bpi/currentprice.json');

    const data = await res.json();

    return {
        bpi: data
    }

}

export default index;
